var Block=function(){this.blockImage=null;this.position=new Vector(100,100);this.velocity=new Vector(0,-0.1);this.speed=10;this.color="rgba(255, 0, 0, 0.3)";this.moving=false;this.boundingRect=new Rect(0,0,0,0)};Block.prototype.setBlockImage=function(a){this.blockImage=a.getImage("blockImage")};Block.prototype.update=function(a){};Block.prototype.draw=function(a){a.drawImage(this.blockImage,this.position.x,this.position.y);a.lineWidth=2;a.fillStyle=this.color;a.strokeStyle="rgb(0, 0, 0)";a.fillRect(this.position.x,this.position.y,this.getWidth(),this.getHeight());a.strokeRect(this.position.x,this.position.y,this.getWidth(),this.getHeight())};Block.prototype.getBoundingRect=function(){width=0,height=0;width=this.getWidth();height=this.getHeight();col_width=width*0.8;col_height=height*0.8;col_x_offset=(width-col_width)/2;col_y_offset=(height-col_height)/2;this.boundingRect.setRect(this.position.x+col_x_offset,this.position.y+col_y_offset,col_width,col_height);return this.boundingRect};Block.prototype.setPosition=function(a,b){this.position.setVector(a,b)};Block.prototype.setVelocity=function(a,b){this.velocity.setVector(a,b)};Block.prototype.getWidth=function(){return this.blockImage.width};Block.prototype.getHeight=function(){return this.blockImage.height};Block.prototype.setColor=function(a){this.color=a};