var BlockPlayer=function(){this.color="rgba(255, 0, 0, 0.2)";this.player_name="enemy";this.position=new Vector(32*4,5);this.width=0;this.height=15;this.minimum_move_rate=1/8;this.current_rate=0;this.maximum_fire_rate=1/2;this.current_fire_rate=0;this.moving=false};BlockPlayer.prototype.draw=function(a){a.fillStyle="rgb(255, 0, 255)";a.fillRect(this.position.x,this.position.y,this.width,this.height);a.fillStyle="rgb(255, 255, 255)";a.font="10px Monaco, Courier";a.fillText(this.player_name,this.position.x+2,this.position.y+10)};BlockPlayer.prototype.updatePosition=function(b,a){this.current_rate=this.current_rate+b;if(this.current_rate>this.minimum_move_rate){this.position.x=a;this.current_rate=0}};BlockPlayer.prototype.fireBlock=function(d,a,c){this.current_fire_rate=this.current_fire_rate+d;if(this.current_fire_rate>this.maximum_fire_rate){var b=new Block();b.color=this.color;b.position=new Vector(this.position.x,this.position.y);b.moving=true;b.setBlockImage(c);a.push(b);this.current_fire_rate=0}};BlockPlayer.prototype.setColor=function(a){this.color=a};